import React,{useState} from 'react'
import {Button,Typography,Grid,Avatar,Container,Paper,TextField} from "@material-ui/core"
import useStyle from "./style.js"
import LockOutlinedIcon from "@material-ui/icons/LockOutlined"
import Input from './Input'
const Auth = () => {
  const classes = useStyle()
  const [showPassword, setShowPassword] = useState(false)

  const isSignUp = false;

  const handleSubmit = () =>{

  }
   const handleChange = () =>{

   }
  const handleShowPassword = () => setShowPassword((prevShowPassword)=>!prevShowPassword);
  return (
    <Container component="main" maxWidth = "xs">
      <Paper className={classes.paper} elevation = {3}>
        <Avatar className={classes.avatar}>
         <LockOutlinedIcon /> 
         <Typography varient = "h5">{isSignUp ? "Sign Up" : "Sign In"}</Typography>
         <form className ={classes.form} onSubmit ={handleSubmit}>
          <Grid container spacing={2}>
            {
              isSignUp && (
                <>

                <Input name = "firstName" label = "First Name" handleChange = {handleChange} autoFocus half/>


                <Input name = "lastName" label = "Last Name" handleChange = {handleChange}  half/>

                </>
              )
            }
            <Input name = "email" lable = "Email Address" handleChange={handleChange} type = "name"/>
            <Input name = "password" lable = "Password" handleChange={handleChange} type = {showPassword ? "text" :"password" } handleShowPassword ={handleShowPassword} />
            {isSignUp && <Input name="confirmPassword" lable= "Repeat Password " />}
            </Grid> 
         </form>
        </Avatar>
      </Paper>

    </Container>
  )
}

export default Auth
