import axios from "axios"
const API = axios.create({baseURL:'http://localhost:5000/'})

export const fetchPosts =() =>API.get(po);
export const createPost = (newPost) =>API.post(po,newPost)
export const updatePost = (id,updatedPost) =>API.patch(`${po}/${id}`,updatedPost)
export const deletePost = (id) => API.delete(`${po}/${id}`)
export const likePost = (id) =>API.patch(`${po}/${id}/likePost`)
