  }
  const [postData, setPostData] = useState({
    creater:"",
    titile :"",
    message :"",
    tag :"",
    selectedFile :""
  });
  const classes = useStyle();

  return (
    <Paper className={classes.paper}>
      <form autoComplete='off' noValidate className={classes.form} onChange ={handleSubmit}>
        <Typography variant='h6'>Creating a Memories</Typography>
        <TextField 
        name='creater' 
        variant='outlined'
         label ='Creater' 
         fullWidth
          value={postData.creater}
           onChange ={(e)=> setPostData({...postData, creater:e.target.value}) } />
           {/* ...postData it is spreding  */}
      </form> 
    </Paper>

  )
}

export default Form
