import React, { useState } from 'react'
import useStyle from "./styles.js"
import { TextField, Button, Typography, Paper, createTheme } from '@material-ui/core';
import FileBase from 'react-file-base64'
import { useDispatch } from 'react-redux';
import { createPost } from '../../actions/posts';




const Form = () => {
  const [postData, setPostData] = useState({creater: "",title: "",message: "",tags: "",selectedFile: ""});
const classes = useStyle();
const dispatch = useDispatch();
const handleSubmit = (e) => {
  e.preventDefault();
dispatch(createPost(postData))
}
const clear = () =>{

}
  return (
    <Paper className={classes.paper}>
      <form autoComplete='off' noValidate className={`${classes.root} ${classes.form}`} onChange={handleSubmit}>
        <Typography variant='h6'>Creating a Memories</Typography>
        <TextField
          name='creater'
          variant='outlined'
          label='Creater'
          fullWidth
          value={postData.creater}
          onChange={(e) => setPostData({ ...postData, creater: e.target.value })} />
        <TextField
          name='title'
          variant='outlined'
          label='Title'
          fullWidth
          value={postData.title}
          onChange={(e) => setPostData({ ...postData, title: e.target.value })} />
        <TextField
          name='message'
          variant='outlined'
          label='Message'
          fullWidth
          value={postData.message}
          onChange={(e) => setPostData({ ...postData, message: e.target.value })} />
        <TextField
          name='tags'
          variant='outlined'
          label='Tags'
          fullWidth
          value={postData.creater}
          onChange={(e) => setPostData({ ...postData, tags: e.target.value })} />
     <div className={classes.fileInput}>
      <FileBase 
      type = "file"
      multiple ={false}
      onDone = {(base64) => setPostData({...postData, selectedFile:base64})}
      />
     </div>
     <Button className={classes.buttonSubmit} variant = 'contained' color='primary' size='large' type='submit' fullWidth> Submit</Button>
     <Button  variant = 'contained' color='secondary' size='small' onClick={clear} fullWidth> Clear</Button>
        {/* ...postData it is spreding  for all we have to just chamge the last property like creter:....  */}
      </form>
    </Paper>

  )
}

export default Form
