import { AUTH } from "../constans/actionType";
import * as api from '../api/index.js';

export const signin = (formData,history) =>async(dis) =>{
    try {
        const {data} = api.signIn(formData);
        dispatch({type:AUTH})
        history.push("/")
    } catch (error) {
        console.log(error)
    }
}

export const signup = (formData,history) =>async(Dispatch) =>{
    try {
        history.push("/")
    } catch (error) {
        console.log(error)
    }
}
